mapper.update:
UPDATE TBROADCAST SET
     	hit = hit + 1
 WHERE foldername = #{param1}

UPDATE TBROADCAST SET
     	hit = hit + 1
 WHERE idx = #{idx}

mapper.select:
	SELECT CATEGORY, GENRE, FOLDERNAME
	  FROM TBROADCAST
	 WHERE 1=1
	   AND CATEGORY LIKE #{param1}
	   AND GENRE LIKE #{param2}
	   AND FOLDERNAME LIKE #{param3}
	   AND (TITLE LIKE #{param4} OR FILENAME LIKE #{param4})
	   AND COUNTRY LIKE #{param5}
  GROUP BY CATEGORY, GENRE, FOLDERNAME
  ORDER BY MAX(PLAY_DATE) DESC, INSERT_DATE DESC

SELECT COUNT(*)
  FROM TBROADCAST
 WHERE 1=1
   AND CATEGORY LIKE #{param1}
   AND GENRE LIKE #{param2}
   AND FOLDERNAME LIKE #{param3}
   AND (TITLE LIKE #{param4} OR FILENAME LIKE #{param4})
   AND COUNTRY LIKE #{param5}

mapper.insert:
INSERT INTO TBROADCAST 
( category, 
  genre, 
  title, 
  grade, 
  foldername, 
  albumname, 
  filename, 
  explanation, 
  file_url, 
  sub_url, 
  country, 
  play_date,
  insert_date,
  priority ) 
  VALUES (
  #{category}
, #{genre}
, #{title}
, #{grade}
, #{foldername}
, #{albumname}
, #{filename}
, #{explanation}
, #{file_url}
, #{sub_url}
, #{country}
, #{play_date}
, #{insert_date}
, #{priority}
)

mapper.select:
SELECT MAX(SEQ_NO) 
  FROM TBROADCAST_COMMENT 
 WHERE idx = #{idx}

SELECT MAX(IDX_NO) 
  FROM TBROADCAST_COMMENT
 WHERE idx = #{idx}

SELECT MAX(SEQ_NO) 
  FROM TBROADCAST_COMMENT 
 WHERE idx = #{param1}
   AND idx_no = #{param2} 
   AND seq_re >= #{param3}
   AND gap > #{param4}

mapper.insert:
INSERT INTO TBOARD (GUBUN, CATEGORY, TITLE, AUTHOR, CONTENT, INSERT_ID) VALUES (
       #{gubun}
     , #{category}
     , #{title}
     , #{author}
     , #{content}
     , #{insert_id}
)

mapper.update:
UPDATE TBOARD_COMMENT
   SET seq_no = seq_no + 1
 WHERE idx = #{param1}
   AND seq_no > #{param2}

mapper.delete:
DELETE 
  FROM TBOARD_COMMENT
 WHERE seq = #{param1}
   AND insert_id = #{param2}

DELETE 
  FROM TBOARD_COMMENT
 WHERE idx = #{param1}
   AND idx_no = #{param2}
   AND seq_re >= #{param3}
   and gap > #{param4}
