mapper.insert:
INSERT INTO TBOARD ( title, author, content ) VALUES (
       #{title}
     , #{author}
     , #{content}
)

mapper.select:
SELECT MAX(SEQ_NO) 
  FROM TBOARD_COMMENT 
 WHERE idx = #{idx}

SELECT MAX(IDX_NO) 
  FROM TBOARD_COMMENT 
 WHERE idx = #{idx}

SELECT MAX(SEQ_NO) 
  FROM TBOARD_COMMENT 
 WHERE idx = #{param1}
   AND idx_no = #{param2} 
   AND seq_re >= #{param3}
   AND gap > #{param4}

mapper.update:
UPDATE TBOARD_COMMENT
   SET seq_no = seq_no + 1
 WHERE idx = #{param1}
   AND seq_no > #{param2}

mapper.delete:
DELETE 
  FROM TBOARD_COMMENT
 WHERE seq = #{param1}
   AND insert_id = #{param2}

DELETE 
  FROM TBOARD_COMMENT
 WHERE idx = #{param1}
   AND idx_no = #{param2}
   AND seq_re >= #{param3}
   and gap > #{param4}
